    public static String convertePonto(String valor) {
        return valor.replace(",", ".");
    }

    public static String inteiroInteligente(double valor) {
        String convertido = "";
        if (valor % 1 > 0) {
            convertido = converteVirgula(String.valueOf(valor));
        } else {
            convertido = String.valueOf((int) valor);
        }
        return (convertido);
    }

    public static double converteDecimal(String valor) {
        double convertido;
        if (valor.equals("")) {
            valor = "0";
        }
        String antesdeconverter = valor;
        String depoisdeconverter = antesdeconverter.replace("R$ ", "").replace(',', '.');
        convertido = Double.parseDouble(depoisdeconverter);
        return (convertido);
    }

    public static String converteVirgula(String valor) {
        String convertido;
        String antesdeconverter = valor;
        String depoisdeconverter = antesdeconverter.replace("R$ ", "").replace('.', ',');
        convertido = depoisdeconverter;
        return (convertido);
    }

    public static double converteDouble(String valor) {
        double convertido;
        if (valor.equals("")) {
            valor = "0";
        }
        String antesdeconverter = valor;
        String depoisdeconverter = antesdeconverter.replace("R$ ", "").replace(".", "");
        depoisdeconverter = depoisdeconverter.replace(',', '.');
        convertido = Double.parseDouble(depoisdeconverter);
        return (convertido);
    }

