    //gera o formato 03.847.655/0001-98 
    public static String converteCNPJ(String valor) {
        StringBuilder convertido = new StringBuilder();
        if (!valor.equals("")) {
            String limpa = valor.replace(".", "").replace("/", "").replace("-", "").replace(" ", "");
            int tamanho = limpa.length();
            if (tamanho == 13) {
                String primeirocaracter = limpa.substring(0, 1);
                String primeirozero = limpa.substring(7, 8);
                if (!primeirocaracter.equals("0") && primeirozero.equals("0")) {
                    limpa = "0" + limpa;
                    tamanho = limpa.length();
                }
            }
            int primeiro = tamanho - 1;
            while (tamanho > 0) {
                if ((tamanho == 2) || (tamanho == 5)) {
                    convertido.insert(0, limpa.substring(primeiro, tamanho) + ".");
                } else {
                    if (tamanho == 8) {
                        convertido.insert(0, limpa.substring(primeiro, tamanho) + "/");
                    } else {
                        if (tamanho == 12) {
                            convertido.insert(0, limpa.substring(primeiro, tamanho) + "-");
                        } else {
                            convertido.insert(0, limpa.substring(primeiro, tamanho));
                        }
                    }
                }

                primeiro--;
                tamanho--;
            }

        }
        return (convertido.toString());
    }
